import React, { useContext, useEffect } from 'react';
import { observer } from "mobx-react-lite";
import { Context } from "../index";
import { fetchTypes, fetchBrands, fetchProducts } from "../http/productAPI";
import TypeBar from "../components/TypeBar";
import BrandBar from "../components/BrandBar";
import DeviceList from "../components/ProductList"; // Змінив імпорт на DeviceList
import Pages from "../components/Pages";
import { Container, Col, Row } from "react-bootstrap";

const Shop = observer(() => {
    const { product } = useContext(Context);

    useEffect(() => {
        fetchTypes().then(data => product.setTypes(data));
        fetchBrands().then(data => product.setBrands(data));
        fetchProducts(null, null, 1, 2).then(data => {
            product.setProducts(data.rows);
            product.setTotalCount(data.count);
        });
    }, [product]);

    useEffect(() => {
        fetchProducts(product.selectedType.id, product.selectedBrand.id, product.page, 2).then(data => {
            product.setProducts(data.rows);
            product.setTotalCount(data.count);
        });
    }, [product.page, product.selectedType, product.selectedBrand, product]);

    return (
        <Container>
            <Row className="mt-2">
                <Col md={3}>
                    <TypeBar />
                </Col>
                <Col md={9}>
                    <BrandBar />
                    <DeviceList /> {/* Виправлення компонента DeviceList */}
                    <Pages />
                </Col>
            </Row>
        </Container>
    );
});

export default Shop;
